INSERT INTO `wp_postmeta` VALUES (7122,1653,'_elementor_data','[{\"id\":\"227739ce\",\"elType\":\"section\",\"settings\":[],\"elements\":[{\"id\":\"6517f09d\",\"elType\":\"column\",\"settings\":{\"_column_size\":100},\"elements\":[{\"id\":\"29cb7250\",\"elType\":\"widget\",\"settings\":{\"editor\":\"<h1><a title=\\\"VILIAPRINT : L\\u2019impression 3D au service du BTP\\\" href=\\\"https:\\/\\/lindustrie40.fr\\/viliaprint-limpression-3d-au-service-du-btp\\/\\\" rel=\\\"bookmark\\\">VILIAPRINT : L\\u2019impression 3D au service du BTP<\\/a><\\/h1><p>\\u00a0<\\/p><div><div><div><div><div><div><div><h2>Une r\\u00e9volution dans la construction de logement gr\\u00e2ce \\u00e0 l\\u2019impression 3D<\\/h2><\\/div><\\/div><div><div><p style=\\\"text-align: justify;\\\">Le projet VILIAPRINT pr\\u00e9sent\\u00e9 par <a href=\\\"https:\\/\\/www.plurial-novilia.fr\\/\\\" target=\\\"_blank\\\" rel=\\\"noopener noreferrer\\\">PLURIAL NOVILIA<\\/a> groupe Action Logement, a pour ambition de faire \\u00e9voluer la construction de logement social en France en int\\u00e9grant pleinement la technologie de l\\u2019impression 3D, et en la faisant passer du stade exp\\u00e9rimental actuel \\u00e0 un stade pleinement op\\u00e9rationnel et reproductible. Comme l\\u2019explique Alain Nicole, Directeur G\\u00e9n\\u00e9ral de PLURIAL NOVILIA, \\u00ab la technologie d\\u2019impression 3D offre des possibilit\\u00e9s quasi-infinies en mati\\u00e8re de construction. Exp\\u00e9riment\\u00e9e depuis un peu moins de 3 ans sur notre secteur, elle est aujourd\\u2019hui essentiellement cantonn\\u00e9e \\u00e0 des \\u00e9l\\u00e9ments de d\\u00e9coration, quelques \\u00e9l\\u00e9ments de construction et quelques \\u00e9l\\u00e9ments porteurs. Notre volont\\u00e9 \\u00e0 travers VILIAPRINT est de pousser la r\\u00e9flexion beaucoup plus loin, en r\\u00e9alisant l\\u2019ensemble des \\u00e9l\\u00e9ments porteurs d\\u2019une construction gr\\u00e2ce \\u00e0 l\\u2019impression 3D \\u2013 et en imaginant un proc\\u00e9d\\u00e9 constructif et un mod\\u00e8le \\u00e9conomique, parfaitement reproductibles \\u00bb.<\\/p><\\/div><\\/div><\\/div><\\/div><\\/div><\\/div><\\/div>\"},\"elements\":[],\"widgetType\":\"text-editor\"},{\"id\":\"1e492f5\",\"elType\":\"widget\",\"settings\":{\"youtube_url\":\"https:\\/\\/www.youtube.com\\/watch?v=6uGcrLDjt6c&feature=emb_title\",\"vimeo_url\":\"https:\\/\\/vimeo.com\\/248368954\",\"dailymotion_url\":\"https:\\/\\/www.dailymotion.com\\/video\\/x6tqhqb\",\"video_type\":\"vimeo\"},\"elements\":[],\"widgetType\":\"video\"}],\"isInner\":false}],\"isInner\":false}]');
